glsl3.vert:
precision highp float;
layout(std140) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_nativeSize;
  mediump vec4 cc_probeInfo;
  mediump vec4 cc_debug_view_mode;
};
layout(std140) uniform CCCamera {
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_surfaceTransform;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
  mediump vec4 cc_nearFar;
  mediump vec4 cc_viewPort;
};
#if USE_LOCAL
    layout(std140) uniform CCLocal {
      highp mat4 cc_matWorld;
      highp mat4 cc_matWorldIT;
      highp vec4 cc_lightingMapUVParam;
      highp vec4 cc_localShadowBias;
      highp vec4 cc_reflectionProbeData1;
      highp vec4 cc_reflectionProbeData2;
      highp vec4 cc_reflectionProbeBlendData1;
      highp vec4 cc_reflectionProbeBlendData2;
    };
#endif
in vec3 a_position;
in vec2 a_texCoord;
in vec4 a_color;
out vec4 color;
out vec2 uv0;
vec4 vert () {
    vec4 pos = vec4(a_position, 1);
    #if USE_LOCAL
        pos = cc_matWorld * pos;
    #endif
    #if USE_PIXEL_ALIGNMENT
        pos = cc_matView * pos;
        pos.xyz = floor(pos.xyz);
        pos = cc_matProj * pos;
    #else
        pos = cc_matViewProj * pos;
    #endif
    uv0 = a_texCoord;
    color = a_color;
    return pos;
}
void main() { gl_Position = vert(); }

glsl3.frag:
precision highp float;
vec4 CCSampleWithAlphaSeparated(sampler2D tex, vec2 uv) {
#if CC_USE_EMBEDDED_ALPHA
  return vec4(texture(tex, uv).rgb, texture(tex, uv + vec2(0.0, 0.5)).r);
#else
  return texture(tex, uv);
#endif
}
#if USE_ALPHA_TEST
  layout(std140) uniform ALPHA_TEST_DATA {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
    if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
    if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 color;
#if USE_TEXTURE
    in vec2 uv0;
    uniform sampler2D cc_spriteTexture;
#endif
layout(std140) uniform args {
    vec4 labelPlusData;
    vec4 outlineColor;
    vec4 shadowColor;
};
float median(float r, float g, float b) {
    return max(min(r, g), min(max(r, g), b));
}
vec4 frag () {
    vec4 o = vec4(1, 1, 1, 1);
    float dilate = labelPlusData.y - 0.5;
    float finalWidget = 0.5 - dilate;
    #if USE_TEXTURE
        vec4 msd = CCSampleWithAlphaSeparated(cc_spriteTexture, uv0);
        float dist = median(msd.r, msd.g, msd.b);
            float smoothing = fwidth(dist);
        float alpha = 1.0;
        #if LABEL_OUTLINE
            float outlineThickness = labelPlusData.x;
            alpha = smoothstep(outlineThickness - smoothing, outlineThickness + smoothing, dist);
            float outlineFactor = smoothstep(finalWidget - smoothing, finalWidget + smoothing, dist);
            o *= mix(vec4(outlineColor.rgb, outlineColor.a * color.a), color, outlineFactor);
        #else
            alpha = smoothstep(finalWidget - smoothing, finalWidget + smoothing, dist);
            o *= color;
        #endif
        #if LABEL_SHADOW
            vec4 shadow_msd = CCSampleWithAlphaSeparated(cc_spriteTexture, uv0 - vec2(labelPlusData.z, -labelPlusData.w));
            float shadowDist = median(shadow_msd.r, shadow_msd.g, shadow_msd.b);
            float shadowSmoothing = smoothing * 2.0;
            #if LABEL_OUTLINE
                float shadowAlpha = smoothstep(labelPlusData.x - shadowSmoothing, labelPlusData.x + shadowSmoothing, shadowDist);
            #else
                float shadowAlpha = smoothstep(finalWidget - shadowSmoothing, finalWidget + shadowSmoothing, shadowDist);
            #endif
            o = mix(vec4(shadowColor.rgb, shadowColor.a * shadowAlpha * color.a), o, alpha);
        #else
            o.a *= alpha;
        #endif
        #if IS_GRAY
            float gray  = 0.2126 * o.r + 0.7152 * o.g + 0.0722 * o.b;
            o.r = o.g = o.b = gray;
        #endif
    #endif
    ALPHA_TEST(o);
    return o;
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

